+document.addEventListener('DOMContentLoaded', function() {
+    (function() {
+        const canvas = document.getElementById('backgroundCanvas');
+        if (!canvas) return;
+        const ctx = canvas.getContext('2d');
+
+        // Resize canvas to fit window
+        function resizeCanvas() {
+            canvas.width = window.innerWidth;
+            canvas.height = window.innerHeight;
+        }
+        window.addEventListener('resize', resizeCanvas);
+        resizeCanvas();
+
+        // Configuration
+        const segmentLength = 100;        // Length of each road segment
+        const roadWidth = 2000;           // Width of the road
+        const cameraHeight = 1000;        // Camera height above road
+        const fov = 100;                  // Field of view for projection
+        const visibleDistance = 20000;    // Distance ahead to render
+        const bufferDistance = 5000;      // Distance behind to keep segments
+        let cameraZ = 0;                  // Camera's Z-position
+        const segments = [];              // Dynamic array of road segments
+
+        // Speed control
+        const baseSpeed = 3000;           // Base movement speed (units/sec)
+        const variation = 500;            // Speed variation (units/sec)
+        let lastTime = 0;                 // Timestamp for delta time
+
+        // Procedural generation functions
+        function getCurve(z) {
+            return Math.sin(z / 500) * 1200 + Math.sin(z / 200) * 200;
+        }
+
+        function getHill(z) {
+            return Math.cos(z / 600) * 600 + Math.cos(z / 250) * 150;
+        }
+
+        // Generate segments between startZ and endZ
+        function generateSegments(startZ, endZ) {
+            for (let z = startZ; z < endZ; z += segmentLength) {
+                const t = z / segmentLength;
+                segments.push({
+                    index: t,
+                    p1: { world: { x: getCurve(z), y: getHill(z), z: z }, screen: {} },
+                    p2: { world: { x: getCurve(z + segmentLength), y: getHill(z + segmentLength), z: z + segmentLength }, screen: {} }
+                });
+            }
+        }
+
+        // Initial segment generation
+        generateSegments(0, visibleDistance);
+
+        // Project 3D world coordinates to 2D screen
+        function project(point, camX, camY, camZ) {
+            const dx = point.world.x - camX;
+            const dy = point.world.y - camY;
+            const dz = point.world.z - camZ;
+            const scale = fov / (dz > 0 ? dz : 0.001); // Avoid division by zero
+            point.screen.x = canvas.width / 2 + scale * dx;
+            point.screen.y = canvas.height / 2 - scale * dy;
+            point.screen.scale = scale;
+        }
+
+        // Draw a line on the canvas
+        function drawLine(x1, y1, x2, y2, alpha = 1) {
+            ctx.globalAlpha = alpha;
+            ctx.beginPath();
+            ctx.moveTo(x1, y1);
+            ctx.lineTo(x2, y2);
+            ctx.stroke();
+        }
+
+        // Render the scene
+        function render(timestamp) {
+            if (lastTime === 0) lastTime = timestamp;
+            const deltaTime = (timestamp - lastTime) / 1000; // Time in seconds
+            lastTime = timestamp;
+
+            // Update camera position with variable speed
+            const speed = baseSpeed + Math.sin(timestamp * 0.001) * variation;
+            cameraZ += speed * deltaTime;
+
+            // Manage segments dynamically
+            const minZ = cameraZ - bufferDistance;
+            const maxZ = cameraZ + visibleDistance;
+            while (segments.length && segments[0].p1.world.z < minZ) {
+                segments.shift(); // Remove segments far behind
+            }
+            const lastSegmentZ = segments[segments.length - 1].p2.world.z;
+            if (lastSegmentZ < maxZ) {
+                generateSegments(lastSegmentZ, maxZ); // Add new segments ahead
+            }
+
+            // Clear and set background
+            ctx.clearRect(0, 0, canvas.width, canvas.height);
+            const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height);
+            gradient.addColorStop(0, '#2a2d34');
+            gradient.addColorStop(0.5, '#1a1c1f');
+            ctx.fillStyle = gradient;
+            ctx.fillRect(0, 0, canvas.width, canvas.height);
+
+            // Set drawing styles
+            ctx.strokeStyle = '#5865F2';
+            ctx.lineWidth = 1;
+            ctx.shadowColor = 'rgba(88, 101, 242, 0.5)';
+            ctx.shadowBlur = 5;
+
+            // Draw road segments
+            for (let i = segments.length - 1; i >= 0; i--) {
+                const segment = segments[i];
+                project(segment.p1, 0, cameraHeight, cameraZ);
+                project(segment.p2, 0, cameraHeight, cameraZ);
+                if (segment.p1.screen.scale <= 0) continue; // Skip if behind camera
+
+                const p1 = segment.p1.screen;
+                const p2 = segment.p2.screen;
+                const width1 = (roadWidth / 2) * p1.scale;
+                const width2 = (roadWidth / 2) * p2.scale;
+
+                drawLine(p1.x - width1, p1.y, p2.x - width2, p2.y); // Left edge
+                drawLine(p1.x + width1, p1.y, p2.x + width2, p2.y); // Right edge
+
+                const midZ = (segment.p1.world.z + segment.p2.world.z) / 2;
+                if (Math.floor(midZ / 200) % 2 === 0) {
+                    drawLine(p1.x, p1.y, p2.x, p2.y); // Center line
+                }
+            }
+
+            ctx.shadowBlur = 0; // Reset shadow
+            requestAnimationFrame(render); // Continue animation
+        }
+
+        // Start the animation
+        requestAnimationFrame(render);
+    })();
+});
