+// background.js
+document.addEventListener('DOMContentLoaded', function() {
+    (function() {
+        // Canvas setup
+        const canvas = document.getElementById('backgroundCanvas');
+        if (!canvas) {
+            console.error('Canvas element not found');
+            return;
+        }
+        const ctx = canvas.getContext('2d');
+
+        // Resize canvas to fit window
+        function resizeCanvas() {
+            canvas.width = window.innerWidth;
+            canvas.height = window.innerHeight;
+        }
+        window.addEventListener('resize', resizeCanvas);
+        resizeCanvas();
+
+        // Configuration constants
+        const segmentLength = 100;
+        const roadWidth = 2000;
+        const cameraHeight = 1000;
+        const fov = 100;
+        const visibleDistance = 20000;
+        const bufferDistance = 5000; // Extra segments ahead
+        let cameraZ = 0;
+        let segments = [];
+        let lastTime = 0;
+        const baseSpeed = 3000; // units per second
+        const variation = 500;  // units per second
+
+        // Key points configuration
+        const keyInterval = 500; // Reduced from 1000 for smoother transitions
+        const keyPoints = new Map();
+        keyPoints.set(0, {curve: 0, hill: 0});
+
+        // Procedural generation functions
+        function hash(z) {
