+// background.js
+document.addEventListener('DOMContentLoaded', function() {
+    (function() {
+        // **Setup Canvas and Context**
+        const canvas = document.getElementById('backgroundCanvas');
+        if (!canvas) {
+            console.error('Canvas element not found');
+            return;
+        }
+        const ctx = canvas.getContext('2d');
+
+        // **Resize Canvas to Fit Window**
+        function resizeCanvas() {
+            canvas.width = window.innerWidth;
+            canvas.height = window.innerHeight;
+        }
+        window.addEventListener('resize', resizeCanvas);
+        resizeCanvas();
+
+        // **Configuration Constants**
+        const segmentLength = 100;      // Length of each road segment
+        const roadWidth = 2000;         // Width of the road
+        const segmentCount = 400;       // Number of segments in the road loop
+        const cameraHeight = 1000;      // Default camera height
+        const fov = 100;                // Field of view for projection
+        const visibleDistance = 20000;  // Distance at which objects are visible
+        let cameraZ = 0;                // Camera position along the z-axis
+        const segments = [];
+
+        // **Speed Control**
+        const baseSpeed = 3000;         // Base speed in units per second
+        const variation = 500;          // Speed variation amplitude
+        let lastTime = 0;               // Timestamp for calculating delta time
+
+        // **Mouse Tracking for Camera Control**
+        let mouseX = 0;                 // Mouse x-coordinate relative to canvas
+        let mouseY = 0;                 // Mouse y-coordinate relative to canvas
+        canvas.addEventListener('mousemove', (event) => {
+            const rect = canvas.getBoundingClientRect();
+            mouseX = event.clientX - rect.left;
+            mouseY = event.clientY - rect.top;
+        });
+
+        // **Camera Movement Variables**
+        let currentCamX = 0;            // Current camera x-position
+        let currentCamY = cameraHeight; // Current camera y-position (height)
+        const smoothingFactor = 0.1;    // Factor for smooth interpolation
+        const maxCamXShift = 1000;      // Maximum horizontal camera shift
+        const minCamY = 500;            // Minimum camera height
+        const maxCamY = 1500;           // Maximum camera height
+
+        // **Procedural Generation Functions**
+        function hash(z) {
+            // Simple hash function for pseudo-randomness
+            return Math.sin(z * 12.9898) * 43758.5453;
+        }
+
+        function shouldPlaceTree(z) {
+            // Determine if a tree should be placed at this z-coordinate
+            return (hash(z) % 1) > 0.7;
+        }
+
+        function getTreeHeight(z) {
+            // Calculate tree height with some variation
+            return 400 + ((hash(z + 1000) % 1) - 0.5) * 200;
+        }
+
+        // **Generate Road Segments**
+        for (let i = 0; i < segmentCount; i++) {
+            const t = (i / segmentCount) * 2 * Math.PI;
+            const noise = Math.sin(t * 5) * 200;
+            segments.push({
+                index: i,
+                p1: { world: { x: 0, y: 0, z: i * segmentLength }, screen: {} },
+                p2: { world: { x: 0, y: 0, z: (i + 1) * segmentLength }, screen: {} },
+                curve: Math.sin(t) * 1200 + noise,         // Horizontal curve
+                hill: Math.cos(t) * 600 + noise * 0.5      // Vertical hill
+            });
+        }
+
+        // **Project 3D Points to 2D Screen**
+        function project(point, camX, camY, camZ) {
+            const dx = point.world.x - camX;
+            const dy = point.world.y - camY;
+            const dz = point.world.z - camZ;
+            const scale = fov / (dz > 0 ? dz : 0.001); // Avoid division by zero
+            point.screen.x = canvas.width / 2 + scale * dx;
+            point.screen.y = canvas.height / 2 - scale * dy;
+            point.screen.scale = scale;
+        }
+
+        // **Draw a Line with Opacity**
+        function drawLine(x1, y1, x2, y2, alpha = 1) {
+            ctx.globalAlpha = alpha;
+            ctx.beginPath();
+            ctx.moveTo(x1, y1);
+            ctx.lineTo(x2, y2);
+            ctx.stroke();
+        }
+
+        // **Render the Scene**
+        function render(timestamp) {
+            // **Calculate Delta Time and Update Camera Z-Position**
+            if (lastTime === 0) lastTime = timestamp;
+            const deltaTime = (timestamp - lastTime) / 1000;
+            lastTime = timestamp;
+            const speed = baseSpeed + Math.sin(timestamp * 0.001) * variation;
+            cameraZ += speed * deltaTime;
+            if (cameraZ >= segmentCount * segmentLength) {
+                cameraZ -= segmentCount * segmentLength; // Loop the road
+            }
+
+            // **Calculate Target Camera Positions Based on Mouse**
+            const targetCamX = (mouseX / canvas.width - 0.5) * 2 * maxCamXShift;
+            const targetCamY = minCamY + (1 - mouseY / canvas.height) * (maxCamY - minCamY);
+
+            // **Smoothly Interpolate Camera Position**
+            currentCamX += (targetCamX - currentCamX) * smoothingFactor;
+            currentCamY += (targetCamY - currentCamY) * smoothingFactor;
+
+            // **Clear Canvas and Draw Gradient Background**
+            ctx.clearRect(0, 0, canvas.width, canvas.height);
+            const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height);
+            gradient.addColorStop(0, '#2a2d34'); // Darker at top
+            gradient.addColorStop(0.5, '#1a1c1f'); // Darker at bottom
+            ctx.fillStyle = gradient;
+            ctx.fillRect(0, 0, canvas.width, canvas.height);
+
+            // **Set Drawing Styles**
+            ctx.strokeStyle = '#5865F2'; // Blue lines
+            ctx.lineWidth = 1;
+
+            // **Add Shadow for Road Lines**
+            ctx.shadowColor = 'rgba(88, 101, 242, 0.5)';
+            ctx.shadowBlur = 5;
+
+            // **Determine Visible Segments**
+            const baseSegmentIdx = Math.floor(cameraZ / segmentLength) % segmentCount;
+
+            // **Draw Road Segments (Back to Front)**
+            for (let i = segmentCount - 1; i >= 0; i--) {
+                const idx = (baseSegmentIdx + i) % segmentCount;
+                const segment = segments[idx];
+                const nextSegment = segments[(segment.index + 1) % segmentCount];
+
+                // Update segment positions with curves and hills
+                segment.p1.world.x = segment.curve;
+                segment.p1.world.y = segment.hill;
+                segment.p2.world.x = nextSegment.curve;
+                segment.p2.world.y = nextSegment.hill;
+
+                // Project points using updated camera position
+                project(segment.p1, currentCamX, currentCamY, cameraZ);
+                project(segment.p2, currentCamX, currentCamY, cameraZ);
+
+                if (segment.p1.screen.scale <= 0) continue; // Skip if behind camera
+
+                const p1 = segment.p1.screen;
+                const p2 = segment.p2.screen;
+                const fade = 1; // Full opacity for simplicity
+
+                // Calculate road width at each point
+                const width1 = (roadWidth / 2) * p1.scale;
+                const width2 = (roadWidth / 2) * p2.scale;
+
+                // Draw road edges and center line
+                drawLine(p1.x - width1, p1.y, p2.x - width2, p2.y, fade); // Left edge
+                drawLine(p1.x + width1, p1.y, p2.x + width2, p2.y, fade); // Right edge
