// Variables
$font-family-base: 'gg sans', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;

// Colors
$color-background: #1a1c1f;
$color-text-primary: #ffffff;
$color-text-secondary: #b9bbbe;
$color-accent: #5865F2;
$color-accent-hover: #4752c4;
$color-input-bg: #202225;
$color-input-focus: #2a2d31;

// Spacing
$spacing-xs: 8px;
$spacing-sm: 16px;
$spacing-md: 24px;
$spacing-lg: 40px;

// Font Weights
$font-weight-regular: 400;
$font-weight-medium: 500;
$font-weight-bold: 700;

// Discord Font (gg sans)
@font-face {
    font-family: 'gg sans';
    src: url('https://cdn.jsdelivr.net/npm/@discord-font/gg-sans@1.0.0/gg-sans-regular.woff2') format('woff2');
    font-weight: 300;
    font-style: normal;
    font-display: swap;
}

@font-face {
    font-family: 'gg sans';
    src: url('https://cdn.jsdelivr.net/npm/@discord-font/gg-sans@1.0.0/gg-sans-medium.woff2') format('woff2');
    font-weight: 500;
    font-style: normal;
    font-display: swap;
}

@font-face {
    font-family: 'gg sans';
    src: url('https://cdn.jsdelivr.net/npm/@discord-font/gg-sans@1.0.0/gg-sans-semibold.woff2') format('woff2');
    font-weight: 600;
    font-style: normal;
    font-display: swap;
}

@font-face {
    font-family: 'gg sans';
    src: url('https://cdn.jsdelivr.net/npm/@discord-font/gg-sans@1.0.0/gg-sans-bold.woff2') format('woff2');
    font-weight: 700;
    font-style: normal;
    font-display: swap;
}

// Reset & Base Styles
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    background-color: $color-background;
    font-family: $font-family-base;
    color: $color-text-primary;
    line-height: 1.5;
    letter-spacing: 0.01em;
}

// Layout & Components
.container {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 1100px;
    width: 100%;
    padding: $spacing-md;
    margin: 0 auto;
    gap: $spacing-lg;
    animation: fadeIn 1s ease-in;
}

.left-section {
    display: flex;
    justify-content: flex-end;
    align-items: center;
    max-width: 45%;

    .logo {
        width: 280px;

        img {
            max-width: 100%;
            height: auto;
            border-radius: $spacing-xs;
        }
    }
}

.vertical-divider {
    width: 3px;
    height: 240px;
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.1), rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.1));
    border-radius: 3px;
}

.right-section {
    flex: 1;
    max-width: 500px;
}

h1, 
h2 {
    font-size: 28px;
    font-weight: $font-weight-bold;
    margin-bottom: $spacing-sm;
    color: $color-text-primary;
    letter-spacing: -0.02em;
}

.tagline {
    font-size: 14px;
    margin-bottom: $spacing-lg - $spacing-sm;
    color: $color-text-secondary;
    font-weight: $font-weight-regular;
    line-height: 1.6;
    letter-spacing: 0.01em;
}

// Buttons & Interactive Elements
.cta {
    display: inline-block;
    background-color: $color-accent;
    color: $color-text-primary;
    text-decoration: none;
    padding: $spacing-xs $spacing-md;
    border-radius: 4px;
    font-size: 16px;
    font-weight: $font-weight-medium;
    transition: all 0.2s ease;
    margin-top: $spacing-sm;
    box-shadow: 0 2px 8px rgba($color-accent, 0.3);
    letter-spacing: 0.01em;

    &:hover {
        background-color: $color-accent-hover;
        box-shadow: 0 4px 12px rgba($color-accent, 0.4);
        transform: translateY(-2px);
    }
}

// Form Styles
.email-signup {
    margin-top: $spacing-lg;
    width: 100%;

    .email-form {
        display: flex;
        max-width: 100%;
    }
}

.email-input {
    flex-grow: 1;
    padding: $spacing-xs $spacing-sm;
    border-radius: 4px 0 0 4px;
    border: 1px solid $color-input-bg;
    font-size: 14px;
    outline: none;
    background-color: $color-input-bg;
    color: $color-text-secondary;
    transition: background-color 0.2s ease, border-color 0.2s ease;
    letter-spacing: 0.01em;

    &:focus {
        background-color: $color-input-focus;
        border-color: $color-accent;
    }
}

.submit-btn {
    background-color: $color-accent;
    color: $color-text-primary;
    border: none;
    padding: $spacing-xs $spacing-md;
    border-radius: 0 4px 4px 0;
    font-weight: $font-weight-medium;
    cursor: pointer;
    transition: background-color 0.2s ease;
    box-shadow: 0 2px 8px rgba($color-accent, 0.3);
    letter-spacing: 0.01em;

    &:hover {
        background-color: $color-accent-hover;
        box-shadow: 0 4px 12px rgba($color-accent, 0.4);
    }
}

// Footer & Social Links
.social-links {
    margin-top: $spacing-md;
    display: flex;
    gap: $spacing-md;

    a {
        color: $color-text-secondary;
        text-decoration: none;
        transition: all 0.2s ease;
        font-size: 14px;
        padding: 4px $spacing-xs;
        border-radius: 4px;
        letter-spacing: 0.01em;

        &:hover {
            color: $color-text-primary;
            background-color: rgba(255, 255, 255, 0.05);
        }
    }
}

// Animations
@keyframes fadeIn {
    from { 
        opacity: 0; 
        transform: translateY(20px); 
    }
    to { 
        opacity: 1; 
        transform: translateY(0); 
    }
}

// Background Animation
#backgroundCanvas {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
    opacity: 0.5;
}

// Accessibility
.screen-reader-text {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

// Responsive Design
@media (max-width: 980px) {
    .container {
        flex-direction: column;
        text-align: center;
        gap: $spacing-md;
    }

    .left-section {
        max-width: 280px;
        justify-content: center;
    }

    .vertical-divider {
        width: 80px;
        height: 3px;
        margin: $spacing-xs 0;
        background: linear-gradient(to right, rgba(255, 255, 255, 0.1), rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.1));
    }

    .right-section {
        max-width: 90%;
    }

    .tagline {
        text-align: center;
        margin-left: auto;
        margin-right: auto;
    }

    .social-links {
        justify-content: center;
    }
}

@media (max-width: 480px) {
    .email-form {
        flex-direction: column;
        gap: $spacing-xs;
    }

    .email-input, 
    .submit-btn {
        width: 100%;
        border-radius: 4px;
    }
}
