+        // Retro wireframe terrain with winding road animation
+        (function() {
+            const canvas = document.getElementById('backgroundCanvas');
+            const ctx = canvas.getContext('2d');
+            
+            // Set canvas to full window size
+            function resizeCanvas() {
+                canvas.width = window.innerWidth;
+                canvas.height = window.innerHeight;
+            }
+            
+            window.addEventListener('resize', resizeCanvas);
+            resizeCanvas();
+            
+            // Wireframe terrain parameters
+            const segmentLength = 50;
+            const roadWidth = 30;
+            const segmentsCount = 30;
+            const horizonY = canvas.height * 0.5;
+            const segments = [];
+            let cameraPosition = 0;
+            
+            // Generate terrain data
+            for (let i = 0; i < segmentsCount; i++) {
+                segments.push({
+                    curve: Math.sin(i * 0.1) * 5,
+                    y: Math.sin(i * 0.5) * 15
+                });
+            }
+            
+            // Animation render loop
+            function render() {
+                ctx.clearRect(0, 0, canvas.width, canvas.height);
+                ctx.strokeStyle = '#5865F2'; // Discord blurple for lines
+                ctx.lineWidth = 1;
+                
+                cameraPosition += 0.05;
+                const basePosition = cameraPosition % 1;
+                
+                // Draw wireframe road and terrain
+                for (let i = 0; i < segmentsCount - 1; i++) {
+                    const currentSegment = segments[(i + Math.floor(cameraPosition)) % segments.length];
+                    const nextSegment = segments[(i + Math.floor(cameraPosition) + 1) % segments.length];
+                    
+                    // Perspective calculations
+                    const scale1 = 1 / (1 + i * 0.1);
+                    const scale2 = 1 / (1 + (i + 1) * 0.1);
+                    
+                    // Current segment points
+                    const x1 = canvas.width / 2 + (currentSegment.curve * scale1 * 200);
+                    const y1 = horizonY + (currentSegment.y * scale1) + (canvas.height * 0.2 * scale1);
+                    const w1 = roadWidth * scale1;
+                    
+                    // Next segment points
+                    const x2 = canvas.width / 2 + (nextSegment.curve * scale2 * 200);
+                    const y2 = horizonY + (nextSegment.y * scale2) + (canvas.height * 0.2 * scale2);
+                    const w2 = roadWidth * scale2;
+                    
+                    // Draw road
+                    ctx.beginPath();
+                    ctx.moveTo(x1 - w1, y1);
+                    ctx.lineTo(x1 + w1, y1);
+                    ctx.lineTo(x2 + w2, y2);
+                    ctx.lineTo(x2 - w2, y2);
+                    ctx.closePath();
+                    ctx.stroke();
+                    
+                    // Draw terrain grid lines
+                    if (i % 3 === 0) {
+                        // Horizontal grid
+                        ctx.beginPath();
+                        for (let j = -10; j <= 10; j += 2) {
+                            const gridX1 = x1 + j * w1;
+                            const gridX2 = x2 + j * w2;
+                            ctx.moveTo(gridX1, y1);
+                            ctx.lineTo(gridX2, y2);
+                        }
+                        ctx.stroke();
+                        
+                        // Vertical terrain lines
+                        ctx.beginPath();
+                        ctx.moveTo(0, y1);
+                        ctx.lineTo(canvas.width, y1);
+                        ctx.stroke();
+                    }
+                }
+                
+                requestAnimationFrame(render);
+            }
+            
+            render();
+        })();
     </script>
 </body>
 </html>

