+        // Wireframe terrain code
+        (function() {
+            const canvas = document.getElementById('backgroundcanvas');
+            const ctx = canvas.getContext('2d');
+            
+            // set canvas to full window size
+            function resizeCanvas() {
+                canvas.width = window.innerWidth;
+                canvas.height = window.innerHeight;
+            }
+            
+            window.addEventListener('resize', resizeCanvas);
+            resizeCanvas();
+            
+            // Parameters for screen coverage
+            const roadWidth = 60;
+            const segmentsCount = 60; // Increased number of segments
+            const horizonY = canvas.height * 0.35; // Moved horizon point higher up
+            const segments = [];
+            let cameraPosition = 0;
+            
+            // Generate terrain data
+            for (let i = 0; i < segmentsCount; i++) {
+                segments.push({
+                    curve: Math.sin(i * 0.1) * 10,
+                    y: Math.sin(i * 0.3) * 25
+                });
+            }
+            
+            function render() {
+                ctx.clearRect(0, 0, canvas.width, canvas.height);
+                ctx.strokeStyle = '#5865F2';
+                ctx.lineWidth = 1;
+                
+                cameraPosition += 0.05;
+                
+                // Draw wireframe road and terrain
+                for (let i = 0; i < segmentsCount - 1; i++) {
+                    const currentSegment = segments[(i + Math.floor(cameraPosition)) % segments.length];
+                    const nextSegment = segments[(i + Math.floor(cameraPosition) + 1) % segments.length];
+                    
+                    // perspective calculations with more gradual scaling
+                    const scale1 = 1 / (1 + i * 0.08); 
+                    const scale2 = 1 / (1 + (i + 1) * 0.08);
+                    
+                    // current segment points with extended depth
+                    const x1 = canvas.width / 2 + (currentSegment.curve * scale1 * 200);
+                    const y1 = horizonY + (currentSegment.y * scale1) + (i * 8 * scale1);
+                    const w1 = roadWidth * scale1;
+                    
+                    // Next segment points
+                    const x2 = canvas.width / 2 + (nextSegment.curve * scale2 * 200);
+                    const y2 = horizonY + (nextSegment.y * scale2) + ((i + 1) * 8 * scale2);
+                    const w2 = roadWidth * scale2;
+                    
+                    // Draw road
+                    ctx.beginPath();
+                    ctx.moveTo(x1 - w1, y1);
+                    ctx.lineTo(x1 + w1, y1);
+                    ctx.lineTo(x2 + w2, y2);
+                    ctx.lineTo(x2 - w2, y2);
+                    ctx.closePath();
+                    ctx.stroke();
+                    
+                    // Draw terrain grid lines
+                    if (i % 3 === 0) {
+                        // Horizontal grid
+                        ctx.beginPath();
+                        for (let j = -10; j <= 10; j += 2) {
+                            const gridX1 = x1 + j * w1;
+                            const gridX2 = x2 + j * w2;
+                            ctx.moveTo(gridX1, y1);
+                            ctx.lineTo(gridX2, y2);
+                        }
+                        ctx.stroke();
+                        
+                        // Vertical terrain lines
+                        ctx.beginPath();
+                        ctx.moveTo(0, y1);
+                        ctx.lineTo(canvas.width, y1);
+                        ctx.stroke();
+                    }
+                }
+                
+                // Draw additional longitudinal lines (parallel to road)
+                const offsets = [-45, -30, -15, 15, 30, 45];
+                for (let offset of offsets) {
+                    ctx.beginPath();
+                    for (let i = 0; i < segmentsCount; i++) {
+                        const segment = segments[(i + Math.floor(cameraPosition)) % segments.length];
+                        const scale = 1 / (1 + i * 0.08);
+                        const x = canvas.width / 2 + ((segment.curve + offset) * scale * 200);
+                        const y = horizonY + (segment.y * scale) + (i * 8 * scale);
+                        if (i === 0) {
+                            ctx.moveTo(x, y);
+                        } else {
+                            ctx.lineTo(x, y);
+                        }
+                    }
+                    ctx.stroke();
+                }
+                
+                requestAnimationFrame(render);
+            }
+            
+            render();
+        })();
