commits.content:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', sans-serif;
  width: 350px;
  padding: 15px;
  background-color: #f8f9fa;
}

.container {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

h1 {
  font-size: 18px;
  margin: 0;
  color: #333;
}

#status-message {
  font-size: 14px;
  color: #555;
  margin: 5px 0;
}

button {
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 8px 16px;
  cursor: pointer;
  font-size: 14px;
  transition: background-color 0.2s;
}

button:hover {
  background-color: #3367d6;
}

button:disabled {
  background-color: #cccccc;
  cursor: not-allowed;
}

#copy-button {
  background-color: #34a853;
}

#copy-button:hover {
  background-color: #2d9144;
}

.json-container {
  display: none;
  flex-direction: column;
  gap: 8px;
}

#json-output {
  width: 100%;
  height: 200px;
  padding: 8px;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-family: monospace;
  resize: none;
  white-space: pre;
  overflow-x: auto;
}
